mapper.select:
select
  out_trade_no, seller_id, total_amount, discountable_amount, un_discountable_amount, subject, body, operator_id, store_id, ali_pay_store_id, terminal_id, timeout_express
from pay_biz_content


        and out_trade_no = #{outTradeNo}


        and seller_id = #{sellerId}


        and total_amount = #{totalAmount}


        and discountable_amount = #{discountableAmount}


        and un_discountable_amount = #{unDiscountableAmount}


        and subject = #{subject}


        and body = #{body}


        and operator_id = #{operatorId}


        and store_id = #{storeId}


        and ali_pay_store_id = #{aliPayStoreId}


        and terminal_id = #{terminalId}


        and timeout_express = #{timeoutExpress}
